/*** @Description:    商务合作（体验账号）* @Author:         TSY* @CreateDate:     2018/10/30 10:34*/<template>    <div>        <div class="top">            <div class="top_content">                <p class="top_title">期待与您合作</p>                <p class="top_subtitle">校鸽的明天，期待您的参与！</p>                <p class="top_subtitle">欢迎志同道合的您的加入！</p>            </div>            <img src="../../assets/about/pic_bg_2.png" alt="">        </div>        <label-input placeholder="请填写您的姓名" v-model="userName"></label-input>        <label-input placeholder="请填写手机号码" v-model="userPhone"></label-input>        <label-input placeholder="请填写邮箱，方便接收产品详细介绍" v-model="userEmail"></label-input>        <label-input placeholder="请填写公司名称" v-model="userCompany"></label-input>        <input-selector class="selector" placeholder="请选择公司所在省市" v-model="address"                        @listenSelect="showAddress"></input-selector>        <p class="tips">温馨提示：提交您的合作意向后，我们会在3个工作日内与您联系，您也可以来电咨询，校鸽热线：0755-86719755。</p>        <div class="commit">提交</div>        <address-picker :is-show="isShowAddress" @click="closeAddress" @addressChange="addressChange"></address-picker>    </div></template><script>    import * as api from '../../constant/api'    import LabelInput from "../common/LabelInput";    import InputSelector from "../common/InputSelector";    import AddressPicker from "../common/AddressPicker";    export default {        name: "Business",        components: {AddressPicker, InputSelector, LabelInput},        data() {            return {                userName: '',                userPhone: '',                userEmail: '',                userCompany: '',                address: '',                isShowAddress: false,//是否显示地址选择器            }        },        mounted() {        },        computed: {},        methods: {            showAddress() {                this.isShowAddress = true;            },            closeAddress(data) {                this.isShowAddress = false;                this.address = data || '';            },            addressChange(data) {                this.address = data || '';            },            commit() {                let params = {                    'userName': this.userName,                    'userPhone': this.userPhone,                    'userEmail': this.userEmail,                    'userCompany': this.userCompany,                    'address': this.address                }                this.http(this, this.user.baseURL + api.GET_SAFE_CHECK_INDEX, params, (res) => {                    if (res.status == 1) {                        this.userName = '';                        this.userPhone = '';                        this.userEmail = '';                        this.userCompany = '';                        this.address = '';                    }                    this.$bottomToast(res.msg);                })            }        }    }</script><style lang="stylus" scoped>    @import "../../style/common.styl"    .top {        position relative    }    .top img {        display block    }    .top_content {        position absolute        width 100%        height 100%        display flex        align-items center        justify-content center        flex-direction column        color white    }    .top_title {        fontSize(60px)        font-weight bold        letter-spacing px2vw(10px)        margin-bottom px2vw(30px)    }    .top_subtitle {        margin px2vw(10px) 0    }    .tips {        font-weight bold        borderTop()        fontSize(28px)        paddingSmall()    }    .commit {        width 90%        margin-left 5%        text-align center        paddingSmall()        background main-color        color white        border-radius 6px        margin-top px2vw(50px)    }    .commit:active {        background (main-color) - 30%    }    .selector /deep/ input {        margin-left px2vw(-10px)    }</style>